import React, { Component } from "react";
import Header from "./components/Header"
import {Card} from "./components/Card"
import Wrapper from './components/Wrapper'

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      gifs: [
        {
          id: 1,
          url: 'https://media.giphy.com/media/e5RONJDZLaIRR5itrT/giphy.gif',
          isclicked: false,

        },
        {
          id: 2,
          url: 'https://media.giphy.com/media/xTiN0tZb3V6QVzScuI/giphy.gif',
          isclicked: false,

        },
        {
          id: 3,
          url: 'https://media.giphy.com/media/3o7aTnQqygA3TcukFi/giphy.gif',
          isclicked: false,

        },
        {
          id: 4,
          url: 'https://media.giphy.com/media/3ohzdNzYZoPbJwLHtC/giphy-downsized.gif',
          isclicked: false,

        },
        {
          id: 5,
          url: 'https://media.giphy.com/media/4Zo1InomnFJWIwIkf7/giphy-downsized.gif',
          isclicked: false,

        },
        {
          id: 6,
          url: 'https://media.giphy.com/media/dIBUivrF5GQJmHx0XQ/giphy.gif',
          isclicked: false,

        },
        {
          id: 7,
          url: 'https://media.giphy.com/media/kiwwko5xwTADDZExtu/giphy.gif',
          isclicked: false,

        },
        {
          id: 8,
          url: 'https://media.giphy.com/media/8YHmeJfN0jWhZqqyBG/giphy.gif',
          isclicked: false,

        },
        {
          id: 9,
          url: 'https://media.giphy.com/media/IQnF0utcFFuiA/giphy-downsized.gif',
          isclicked: false,

        },
        {
          id: 10,
          url: 'https://media.giphy.com/media/oX9RhCMF6luPe6hxvj/giphy-downsized.gif',
          isclicked: false,

        },
        {
          id: 11,
          url: 'https://media.giphy.com/media/BslY6QeNBqEHm/giphy-downsized.gif',
          isclicked: false,

        },
        {
          id: 12,
          url: 'https://media.giphy.com/media/fs62ZydKjn6lbJ0pAU/giphy-downsized.gif',
          isclicked: false,

        }
      ],

      score: 0,
      topScore: 0,

    }


  }

  handleClick = e => {
    e.preventDefault()
    let thisGif = e.target.id;
    let newMappedArray = this.state.gifs.map( (gif) => {
        if(gif.id == thisGif) {
          console.log(gif)
          gif.isclicked = true
        } else {
          console.log('false')

        }

        this.setState({
        gif: newMappedArray
      })



      // if(gif.id == thisGif){
      //   gif.isclicked = true;
      // } else if ( gif.isclicked === true) {
      //  console.log('truth clicked')
      // }


    })




    const newShuffle = this.state.gifs.sort(function () { return 0.5 - Math.random() });
    this.setState(prevState => {
      return {
        gifs: newShuffle,
        score: prevState.score + 1,
        topScore: prevState.topScore + 1,



      }


    })

  }



  render() {
    return (
      <div>
        <Header 
          score={this.state.score}
          topScore={this.state.topScore}
        />
        <Wrapper>
          {this.state.gifs.map((gif) => 
            <Card
              isclicked={gif.isclicked}
              key={gif.id}
              id={gif.id}
              url={gif.url}
              onClick={this.handleClick}
            />
          )}

        </Wrapper>
      </div>




    )
  }
}




export default App;
