        },
        {
          id: 12,
          url: 'https://media.giphy.com/media/fs62ZydKjn6lbJ0pAU/giphy-downsized.gif',


        }
      ],


      topScore: 0,
      numberBucket: [],


    }


  }

  handleClick = id => {
    const newShuffle = this.state.gifs.sort(function () { return 0.5 - Math.random() })
    console.log('clicked', id)
    this.setState(prevState => {
      return {
        gifs: newShuffle,
        numberBucket: prevState.numberBucket.concat(id),
        score: prevState.score + 1,
        topScore: prevState.topScore + 1,
      }
    });



    this.setState(prevState =>{
      const updatedBool = prevState.gifs.map(gif => {
        if(gif.id === id){
          gif.isclicked = !gif.isclicked
        }
        return gif
      })
      return {
        gifs: updatedBool
      }
    })










  }


