  //     }
  //     return gif
  //   })
  //   return {
  //     score: 0,
  //     gifs: newerArray
  //   }
  // })
  // 















  render() {
    return (
      <div>
        <Header
          score={this.state.numberBucket.length}
          topScore={this.state.topScore}
        />
        <Wrapper>
          {this.state.gifs.map((gif) =>
            <Card
              isclicked={gif.isclicked}
              key={gif.id}
              id={gif.id}
              url={gif.url}
              handleClick={this.handleClick}
            />
          )}

        </Wrapper>
      </div>




    )
  }
}




export default App;
